import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header/Header";

function RedStar() {
  return <span style={{ color: 'red' }}>*</span>;
}

const InstituteApplicationForm = () => {
  const navigate = useNavigate();
  const [showConfirmationPopup, setShowConfirmationPopup] = useState(false);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [applicationId, setApplicationId] = useState('');
  const [error, setError] = useState('');

  // Define state for form inputs
  const [heid, setHeid] = useState('');
  const [pan, setPan] = useState('');
  const [tan, setTan] = useState('');
  const [email, setEmail] = useState('');
  const [collegeName, setCollegeName] = useState('');
  const [address, setAddress] = useState('');
  const [city, setCity] = useState('');
  const [mobNum, setMobNum] = useState('');
  const [state, setState] = useState('');

  const handleRegisterClick = () => {
    if (!heid || !pan || !tan || !email || !collegeName || !address || !city || !mobNum || !state) {
      setError('Please fill all the input fields.');
      return;
    }
    setError('');
    setShowConfirmationPopup(true);
  };

  const generateApplicationId = () => {
    const stateInitials = state.slice(0, 2).toUpperCase(); // Get first two letters of state
    const heiInitials = heid.slice(0, 2).toUpperCase(); // Get first two letters of HEI_id
    const randomNumbers = Math.floor(100000 + Math.random() * 900000); // Generate random 6-digit number
    const randomId = `EI_${stateInitials}_${heiInitials}${randomNumbers}`; // Concatenate 'EI_', state initials, HEI initials, and random number
    return randomId;
  };

  const handleConfirmRegistration = async () => {
    setShowConfirmationPopup(false);
    try {
      const newApplicationId = generateApplicationId();
      setApplicationId(newApplicationId);

      const formData = {
        heid,
        pan,
        tan,
        email,
        collegeName,
        address,
        city,
        state,
        mobNum,
        appId: newApplicationId,
      };

      // Simulating API call with a timeout
      setTimeout(async () => {
        // Replace with actual API call once implemented
        // const response = await axios.post(`http://localhost:4000/institute/register-institute`, formData);

        const response = { data: { success: true } }; // Simulated response for success
        if (response.data.success === true) {
          setShowSuccessPopup(true);
          setTimeout(() => {
            setShowSuccessPopup(false); // Close success popup after 10 seconds
            navigate("/institute-login");
          }, 10000);
        } else {
          alert("Institute registration failed!");
        }
      }, 1000); // Simulating API delay
    } catch (error) {
      console.error("Error registering institute:", error);
    }
  };

  const handleCancel = () => {
    setShowConfirmationPopup(false);
  };

  return (
    <>
      <Header />
      <div className="container card-form shadow mt-4 p-4" style={{ fontFamily: 'Cambria, serif' }}>
        <div className="card-body bg-white">
          <p className="text-center themeFontcolor" style={{ fontSize: "20px", backgroundColor: "#115a87", color: "#fff", padding: "20px" }}>
            <strong>Institute Application Form</strong>
          </p>
          <hr />
          <div className="card-form shadow p-4 mt-4">
            <div className="row">
              {error && <div className="alert alert-danger" role="alert">{error}</div>}
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Enter HEI_id <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter your HEI_id"
                  required
                  value={heid}
                  onChange={(e) => setHeid(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>PAN number <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter PAN number"
                  required
                  value={pan}
                  onChange={(e) => setPan(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>TAN number <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter TAN number"
                  required
                  value={tan}
                  onChange={(e) => setTan(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Mobile Number <RedStar /></label>
                <input
                  type="tel"
                  placeholder="Enter Mobile Number"
                  required
                  value={mobNum}
                  onChange={(e) => setMobNum(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Official E-mail <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Official Email"
                  required
                  value={email}
                  onChange={(e) => setEmail(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>College Name <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter College name"
                  required
                  value={collegeName}
                  onChange={(e) => setCollegeName(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Address <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Address"
                  required
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>City <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter City"
                  required
                  value={city}
                  onChange={(e) => setCity(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>State <RedStar /></label>
                <select
                  required
                  value={state}
                  onChange={(e) => setState(e.target.value)}
                  style={{ display: "block", width: "100%" }}
                >
                  <option value="" disabled>Select State</option>
                  <option value="MH">MH - Maharashtra</option>
                  <option value="UK">UK - Uttarakhand</option>
                  <option value="AP">AP - Andhra Pradesh</option>
                  <option value="AR">AR - Arunachal Pradesh</option>
                  <option value="AS">AS - Assam</option>
                  <option value="BR">BR - Bihar</option>
                  <option value="CG">CG - Chhattisgarh</option>
                  <option value="CH">CH - Chandigarh</option>
                  <option value="DL">DL - Delhi</option>
                  <option value="GA">GA - Goa</option>
                  <option value="GJ">GJ - Gujarat</option>
                  <option value="HP">HP - Himachal Pradesh</option>
                  <option value="HR">HR - Haryana</option>
                  <option value="JH">JH - Jharkhand</option>
                  <option value="KA">KA - Karnataka</option>
                  <option value="KL">KL - Kerala</option>
                  <option value="ML">ML - Meghalaya</option>
                  <option value="MN">MN - Manipur</option>
                  <option value="MZ">MZ - Mizoram</option>
                  <option value="NL">NL - Nagaland</option>
                  <option value="OD">OD - Odisha</option>
                  <option value="PB">PB - Punjab</option>
                  <option value="RJ">RJ - Rajasthan</option>
                  <option value="SK">SK - Sikkim</option>
                  <option value="TN">TN - Tamil Nadu</option>
                  <option value="TR">TR - Tripura</option>
                  <option value="UP">UP - Uttar Pradesh</option>
                  <option value="WB">WB - West Bengal</option>
                </select>
              </div>
              <button
                type="button"
                className="btn btn-primary"
                style={{ marginTop: "20px", padding: "10px 20px", backgroundColor: "#007bff", color: "#fff", border: "none", borderRadius: "5px" }}
                onClick={handleRegisterClick}
              >
                Register
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Confirmation Popup */}
      {showConfirmationPopup && (
        <div className="popup-overlay" style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="popup" style={{
            background: '#fff',
            padding: '20px',
            borderRadius: '8px',
            width: '80%',
            maxWidth: '500px',
            textAlign: 'center'
          }}>
            <p>Are you sure you want to register this institute with the following details?</p>
            <ul>
              <li><strong>HEI_id:</strong> {heid}</li>
              <li><strong>PAN number:</strong> {pan}</li>
              <li><strong>TAN number:</strong> {tan}</li>
              <li><strong>Email:</strong> {email}</li>
              <li><strong>College Name:</strong> {collegeName}</li>
              <li><strong>Address:</strong> {address}</li>
              <li><strong>City:</strong> {city}</li>
              <li><strong>State:</strong> {state}</li>
              <li><strong>Mobile Number:</strong> {mobNum}</li>
              <li><strong>Application ID:</strong> {applicationId}</li>
            </ul>
            <div className="text-center">
              <button
                className="btn btn-success"
                style={{
                  margin: '10px',
                  padding: '10px 20px',
                  backgroundColor: '#28a745',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '5px'
                }}
                onClick={handleConfirmRegistration}
              >
                Confirm
              </button>
              <button
                className="btn btn-secondary"
                style={{
                  margin: '10px',
                  padding: '10px 20px',
                  backgroundColor: '#6c757d',
                  color: '#fff',
                  border: 'none',
                  borderRadius: '5px'
                }}
                onClick={handleCancel}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Success Popup */}
      {showSuccessPopup && (
        <div className="popup-overlay" style={{
          position: 'fixed',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          background: 'rgba(0, 0, 0, 0.5)',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          zIndex: 1000
        }}>
          <div className="popup" style={{
            background: '#fff',
            padding: '20px',
            borderRadius: '8px',
            width: '80%',
            maxWidth: '500px',
            textAlign: 'center'
          }}>
            <p>Registration successful! You will be redirected to the login page in a few seconds.</p>
          </div>
        </div>
      )}
    </>
  );
};

export default InstituteApplicationForm;
