const noticeBoardsData = [
  {
    headingText: "Students",
    data: [
      {
        text: [
          "Students look for latest updates and new schemes",
          "Private organisations are encouraged to join with Intelligent Scholarship Portal",
          "Institutes can check for valid student data",
        ],
        linkText: "Click for more information",
        link: "https://example.com",
      },
    ],
  }
];

export default noticeBoardsData;
