import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header/Header";

function RedStar() {
  return <span style={{ color: 'red' }}>*</span>;
}

const states = [
  { value: "", label: "Select State" },
  { value: "MH", label: "MH - Maharashtra" },
  { value: "UK", label: "UK - Uttarakhand" },
  { value: "AP", label: "AP - Andhra Pradesh" },
  { value: "AR", label: "AR - Arunachal Pradesh" },
  { value: "AS", label: "AS - Assam" },
  { value: "BR", label: "BR - Bihar" },
  { value: "CG", label: "CG - Chhattisgarh" },
  { value: "CH", label: "CH - Chandigarh" },
  { value: "DL", label: "DL - Delhi" },
  { value: "GA", label: "GA - Goa" },
  { value: "GJ", label: "GJ - Gujarat" },
  { value: "HP", label: "HP - Himachal Pradesh" },
  { value: "HR", label: "HR - Haryana" },
  { value: "JH", label: "JH - Jharkhand" },
  { value: "KA", label: "KA - Karnataka" },
  { value: "KL", label: "KL - Kerala" },
  { value: "ML", label: "ML - Meghalaya" },
  { value: "MN", label: "MN - Manipur" },
  { value: "MP", label: "MP - Madhya Pradesh" },
  { value: "MZ", label: "MZ - Mizoram" },
  { value: "NL", label: "NL - Nagaland" },
  { value: "OD", label: "OD - Odisha" },
  { value: "PB", label: "PB - Punjab" },
  { value: "PY", label: "PY - Pondicherry" },
  { value: "RJ", label: "RJ - Rajasthan" },
  { value: "SK", label: "SK - Sikkim" },
  { value: "TN", label: "TN - Tamil Nadu" },
  { value: "TR", label: "TR - Tripura" },
  { value: "TS", label: "TS - Telangana" },
  { value: "UP", label: "UP - Uttar Pradesh" },
  { value: "WB", label: "WB - West Bengal" }
];

const InstituteApplicationForm = () => {
  const navigate = useNavigate();
  const [formData, setFormData] = useState({
    heid: '',
    pan: '',
    tan: '',
    email: '',
    collegeName: '',
    address: '',
    city: '',
    mobNum: '',
    state: ''
  });
  const [error, setError] = useState('');
  const [showConfirmationPopup, setShowConfirmationPopup] = useState(false);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [applicationId, setApplicationId] = useState('');

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prevFormData => ({
      ...prevFormData,
      [name]: value
    }));
  };

  const handleRegisterClick = () => {
    const { heid, pan, tan, email, collegeName, address, city, mobNum, state } = formData;
    if (!heid || !pan || !tan || !email || !collegeName || !address || !city || !mobNum || !state) {
      setError('Please fill all the input fields.');
      return;
    }
    setError('');
    setShowConfirmationPopup(true);
  };

  const generateApplicationId = () => {
    const stateInitials = formData.state.slice(0, 2).toUpperCase();
    const heiInitials = formData.heid.slice(0, 2).toUpperCase();
    const randomNumbers = Math.floor(100000 + Math.random() * 900000);
    return `EI_${stateInitials}_${heiInitials}${randomNumbers}`;
  };

  const handleConfirmRegistration = async () => {
    setShowConfirmationPopup(false);
    try {
      const newApplicationId = generateApplicationId();
      setApplicationId(newApplicationId);

      const response = await axios.post(`http://localhost:4000/institute/register-institute`, { ...formData, appId: newApplicationId });

      if (response.data.success) {
        setShowSuccessPopup(true);
        setTimeout(() => {
          setShowSuccessPopup(false);
          navigate("/institute-login");
        }, 10000);
      } else {
        setError("Institute registration failed!");
      }
    } catch (error) {
      console.error("Error registering institute:", error);
      setError("Error registering institute. Please try again.");
    }
  };

  const handleCancel = () => {
    setShowConfirmationPopup(false);
  };

  return (
    <>
      <Header />
      <div className="container card-form shadow mt-4 p-4" style={{ fontFamily: 'Cambria, serif' }}>
        <div className="card-body bg-white">
          <p className="text-center themeFontcolor" style={{ fontSize: "20px", backgroundColor: "#115a87", color: "#fff", padding: "20px" }}>
            <strong>Institute Application Form</strong>
          </p>
          <hr />
          <div className="card-form shadow p-4 mt-4">
            <div className="row">
              {error && <div className="alert alert-danger" role="alert">{error}</div>}
              {[
                { label: "Enter HEI_id", name: "heid", value: formData.heid },
                { label: "PAN number", name: "pan", value: formData.pan },
                { label: "TAN number", name: "tan", value: formData.tan },
                { label: "Mobile Number", name: "mobNum", value: formData.mobNum, type: "tel" },
                { label: "Official E-mail", name: "email", value: formData.email },
                { label: "College Name", name: "collegeName", value: formData.collegeName },
                { label: "Address", name: "address", value: formData.address },
                { label: "City", name: "city", value: formData.city }
              ].map((field, index) => (
                <div key={index} className="col-sm-5 mb-3">
                  <label style={{ display: "block" }}>{field.label} <RedStar /></label>
                  <input
                    type={field.type || "text"}
                    placeholder={`Enter ${field.label}`}
                    required
                    name={field.name}
                    value={field.value}
                    onChange={handleChange}
                  />
                </div>
              ))}
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>State <RedStar /></label>
                <select
                  required
                  name="state"
                  value={formData.state}
                  onChange={handleChange}
                  style={{ display: "block", width: "100%" }}
                >
                  {states.map(({ value, label }) => (
                    <option key={value} value={value}>{label}</option>
                  ))}
                </select>
              </div>
              <button
                type="button"
                className="btn btn-md btn-primary"
                style={{ marginTop: "20px", paddingTop: "5px", paddingRight: "10px", height: "50px", width: "90px", marginLeft: "50px", display: "block", fontWeight: "bold" }}
                onClick={handleRegisterClick}
              >
                Register
              </button>
            </div>
          </div>
          {showConfirmationPopup && (
            <div className="popup">
              <div className="popup-inner">
                <h2>Confirm Application</h2>
                <p>Are you sure you want to register the application?</p>
                <button className="btn btn-md btn-primary" onClick={handleConfirmRegistration} style={{ marginRight: "10px" }}>Yes</button>
                <button className="btn btn-md btn-secondary" onClick={handleCancel}>No</button>
              </div>
            </div>
          )}
          {showSuccessPopup && (
            <div className="popup">
              <div className="popup-inner">
                <h2>Registration Successful</h2>
                <p>Your Application ID: {applicationId}</p>
                <p>Redirecting to Institute Login...</p>
              </div>
            </div>
          )}
        </div>
      </div>
    </>
  );
};

export default InstituteApplicationForm;
