import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios";
import Header from "../components/Header/Header";

function RedStar() {
  return <span style={{ color: 'red' }}>*</span>;
}

const ApplicantApplicationForm = () => {
  const navigate = useNavigate();
  const [showConfirmationPopup, setShowConfirmationPopup] = useState(false);
  const [showSuccessPopup, setShowSuccessPopup] = useState(false);
  const [applicationId, setApplicationId] = useState('');

  // Define state for form inputs
  const [aadharNo, setAadharNo] = useState('');
  const [mobileNo, setMobileNo] = useState('');
  const [applicationNo, setApplicationNo] = useState('');
  // const [bankDetails, setBankDetails] = useState('');
  const [ifsc, setIfsc] = useState('');
  const [name, setName] = useState('');
  const [dob, setDob] = useState('');
  const [address, setAddress] = useState('');
  const [panCard, setPanCard] = useState('');
  const [aadharStatus, setAadharStatus] = useState('');
  const [aadharBankLink, setAadharBankLink] = useState('');
  const [digilockerQr, setDigilockerQr] = useState(null);

  const handleRegisterClick = () => {
    setShowConfirmationPopup(true);
  };

  const generateApplicationId = () => {
    const randomNumbers = Math.floor(1000 + Math.random() * 9000); // Generate random 4-digit number
    const randomId = 'APP${randomNumbers}'; // Concatenate 'APP' and random number
    return randomId;
  };

  const handleConfirmRegistration = async () => {
    setShowConfirmationPopup(false);
    try {
      const newApplicationId = generateApplicationId();
      setApplicationId(newApplicationId);

      const formData = {
        name,
        dob,
        mobileNo,
        address,
        aadharNo,
        applicationNo,
        ifsc,
        panCard,
        digilockerQr,
        appId: newApplicationId,
      };

      // const formData = new FormData();
      // formData.append('aadharNo', aadharNo);
      // formData.append('mobileNo', mobileNo);
      // formData.append('applicationNo', applicationNo);
      // // formData.append('bankDetails', bankDetails);
      // formData.append('ifsc', ifsc);
     
      // formData.append('address', address);
      // formData.append('panCard', panCard);
      // formData.append('aadharStatus', aadharStatus);
      // formData.append('aadharBankLink', aadharBankLink);
      // formData.append('digilockerQr', digilockerQr);
      // formData.append('appId', newApplicationId);

      // Simulating API call with a timeout
      setTimeout(async () => {
        // Replace with actual API call once implemented
        const response = await axios.post(`http://localhost:4000/applicant/register-appicant`, formData);

        // const response = { data: { success: true } }; // Simulated response for success

        if (response.data.success === true) {
          setShowSuccessPopup(true);
          setTimeout(() => {
            setShowSuccessPopup(false); // Close success popup after 10 seconds
            navigate("/applicant-login");
          }, 10000);
        } else {
          alert("Applicant registration failed!");
        }
      }, 1000); // Simulating API delay
    } catch (error) {
      console.error("Error registering applicant:", error);
    }
  };

  const handleCancel = () => {
    setShowConfirmationPopup(false);
  };

  return (
    <>
      <Header />
      <div className="container card-form shadow mt-4 p-4" style={{ fontFamily: 'Cambria, serif' }}>
        <div className="card-body bg-white">
          <p className="text-center themeFontcolor" style={{ fontSize: "20px", backgroundColor: "#115a87", color: "#fff", padding: "20px" }}>
            <strong>Applicant Application Form</strong>
          </p>
          <hr />
          <div className="card-form shadow p-4 mt-4">
            <div className="row">
            {error && <div className="alert alert-danger" role="alert">{error}</div>}
              {/* Form fields */}
              {[
                { label: "Enter HEI_id", value: heid, onChange: setHeid },
                { label: "PAN number", value: pan, onChange: setPan },
                { label: "TAN number", value: tan, onChange: setTan },
                { label: "Mobile Number", value: mobNum, onChange: setMobNum, type: "tel" },
                { label: "Official E-mail", value: email, onChange: setEmail },
                { label: "College Name", value: collegeName, onChange: setCollegeName },
                { label: "Address", value: address, onChange: setAddress },
                { label: "City", value: city, onChange: setCity }
              ].map((field, index) => (
                <div key={index} className="col-sm-5 mb-3">
                  <label style={{ display: "block" }}>{field.label} <RedStar /></label>
                  <input
                    type={field.type || "text"}
                    placeholder={`Enter ${field.label}`}
                    required
                    name={field.label.toLowerCase().replace(/ /g, "")}
                    value={field.value}
                    onChange={(e) => field.onChange(e.target.value)}
                  />
                </div>
              ))}












              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Name of Applicant <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Name"
                  required
                  value={name}
                  onChange={(e) => setName(e.target.value)}
                />
              </div>

              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Application Number <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Application Number"
                  required
                  value={applicationNo}
                  onChange={(e) => setApplicationNo(e.target.value)}
                />
              </div>

              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Date of Birth <RedStar /></label>
                <input
                  type="date"
                  required
                  value={dob}
                  onChange={(e) => setDob(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Bank Account Number or IFSC Code <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Bank Account Number and IFSC Code"
                  required
                  value={ifsc}
                  onChange={(e) => setIfsc(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Mobile Number <RedStar /></label>
                <input
                  type="tel"
                  placeholder="Enter Mobile Number"
                  required
                  value={mobileNo}
                  onChange={(e) => setMobileNo(e.target.value)}
                />
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Father/Mother/Guardian PAN Card Number <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter PAN Card Number"
                  required
                  value={panCard}
                  onChange={(e) => setPanCard(e.target.value)}
                />
              </div>

              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Address <RedStar /></label>
                <textarea
                  placeholder="Enter Address"
                  required
                  value={address}
                  onChange={(e) => setAddress(e.target.value)}
                />
              </div>
              {/* <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Aadhar Bank Link <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Aadhar Bank Link Status"
                  required
                  value={aadharBankLink}
                  onChange={(e) => setAadharBankLink(e.target.value)}
                />
              </div> */}
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Aadhar Number <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Aadhar Number"
                  required
                  value={aadharNo}
                  onChange={(e) => setAadharNo(e.target.value)}
                />
              </div>
             
              
            
              {/* <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Aadhar Status <RedStar /></label>
                <input
                  type="text"
                  placeholder="Enter Aadhar Status"
                  required
                  value={aadharStatus}
                  onChange={(e) => setAadharStatus(e.target.value)}
                />
              </div> */}
              
              <div className="col-sm-12 mb-3">
                <p style={{ fontSize: "16px", fontWeight: "bold" }}>
                  Note: Refer to this <a href="https://www.youtube.com/shorts/oCHrfJKd1-c" target="_blank" rel="noopener noreferrer">video</a> on how to access the QR code.
                </p>
              </div>
              <div className="col-sm-5 mb-3">
                <label style={{ display: "block" }}>Digilocker QR Code <RedStar /></label>
                <input
                  type="file"
                  accept="application/pdf"
                  required
                  onChange={(e) => setDigilockerQr(e.target.files[0])}
                />
              </div>
              <button
                type="button"
                className="btn btn-md btn-primary"
                style={{ marginTop: "20px", paddingTop: "5px", paddingRight: "10px", height: "50px", width: "90px", marginLeft: "50px", display: "block", fontWeight: "bold" }}
                onClick={handleRegisterClick}
              >
                Register
              </button>
            </div>
          </div>
          {showConfirmationPopup && (
            <div className="popup">
              <div className="popup-inner">
                <h2>Confirm Application</h2>
                <p>Are you sure you want to register the application?</p>
                <button className="btn btn-md btn-primary" onClick={handleConfirmRegistration} style={{ marginRight: "10px" }}>Yes</button>
                <button className="btn btn-md btn-secondary" onClick={handleCancel}>No</button>
              </div>
            </div>
          )}
          {showSuccessPopup && (
            <div className="popup">
              <div className="popup-inner">
                <h2>Registration Successful</h2>
                <p>Your Application ID: {applicationId}</p>
                <button className="btn btn-md btn-primary" onClick={() => setShowSuccessPopup(false)}>Close</button>
              </div>
            </div>
          )}
        </div>
      </div>
      <style jsx>{`
        .popup {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: rgba(0, 0, 0, 0.5);
          display: flex;
          justify-content: center;
          align-items: center;
        }

        .popup-inner {
          background-color: #fff;
          padding: 20px;
          border-radius: 10px;
          text-align: center;
        }

        .btn-secondary {
          background-color: #6c757d;
          border-color: #6c757d;
        }
      `}</style>
    </>
  );
}

export default ApplicantApplicationForm;
