const AllSchemes = () => {
  const [schemes, setSchemes] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
      const fetchSchemes = async () => {
          try {
              const response = await axios.get('http://localhost:4000/api/all-schemes');
              setSchemes(response.data);
          } catch (error) {
              setError('Error fetching schemes');
              console.error("Error fetching schemes:", error);
          } finally {
              setLoading(false);
          }
      };

      fetchSchemes();
  }, []);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
      <div className="container">
          <h4>All Available Scholarships</h4>
          {schemes.length > 0 ? (
              <table className="table">
                  <thead>
                      <tr>
                          <th>Scheme ID</th>
                          <th>Scheme Name</th>
                          <th>Income Criteria</th>
                          <th>Field of Study</th>
                          <th>Start Date</th>
                          <th>End Date</th>
                      </tr>
                  </thead>
                  <tbody>
                      {schemes.map((scheme) => (
                          <tr key={scheme.sch_id}>
                              <td>{scheme.sch_id}</td>
                              <td>{scheme.sch_name}</td>
                              <td>{scheme.income_criteria}</td>
                              <td>{scheme.field_of_study}</td>
                              <td>{new Date(scheme.start_date).toLocaleDateString()}</td>
                              <td>{new Date(scheme.end_date).toLocaleDateString()}</td>
                          </tr>
                      ))}
                  </tbody>
              </table>
          ) : (
              <p>No schemes available!</p>
          )}
      </div>
  );
};

export default AllSchemes;
